function run(identity, servico, time) {
  const cleanIdentity = identity.split("@")[0];
  const dateParts = time.split(" "); // Divide a string em data e hora
  const date = dateParts[0].split("/");
  const time = dateParts[1].split(":");

  const year = parseInt(date[2], 10);
  const month = parseInt(date[1], 10) - 1;
  const day = parseInt(date[0], 10);
  const hour = parseInt(time[0], 10);
  const minute = parseInt(time[1], 10);
  const second = parseInt(time[2], 10);

  const dateStart = new Date(year, month, day, hour, minute, second);
  const dateEnd = new Date(dateStart);
  dateEnd.setHours(dateEnd.getHours() + 1);

  return {
    "summary": `${cleanIdentity} ${servico}`,
    "start": { "dateTime": formatDateTime(dateStart)},
    "end": { "dateTime": formatDateTime(dateEnd)}
  };
}
function formatDateTime(date) {
  const year = date.getFullYear();
  const month = (date.getMonth() + 1).toString().padStart(2, '0');
  const day = date.getDate().toString().padStart(2, '0');
  const hour = date.getHours().toString().padStart(2, '0');
  const minute = date.getMinutes().toString().padStart(2, '0');
  const second = date.getSeconds().toString().padStart(2, '0');
  const timeZoneOffset = -date.getTimezoneOffset() / 60;

  const offsetSign = timeZoneOffset >= 0 ? "+" : "-";
  const offsetHours = Math.abs(Math.floor(timeZoneOffset)).toString().padStart(2, '0');
  const offsetMinutes = (Math.abs(timeZoneOffset) % 1 * 60).toString().padStart(2, '0');

  const formattedDate = `${year}-${month}-${day}T${hour}:${minute}:${second}${offsetSign}${offsetHours}:${offsetMinutes}`;
  return formattedDate;
}

